import React, { useEffect, useState, useRef } from 'react';

interface AddressAutocompleteProps {
  label: string;
  id: string;
  value: string;
  onChange: (value: string) => void;
  placeholder?: string;
  required?: boolean;
}

declare global {
  interface Window {
    google: any;
  }
}

const AddressAutocomplete: React.FC<AddressAutocompleteProps> = ({
  label,
  id,
  value,
  onChange,
  placeholder = 'Enter an address',
  required = false,
}) => {
  const inputRef = useRef<HTMLInputElement>(null);
  const [autocomplete, setAutocomplete] = useState<google.maps.places.Autocomplete | null>(null);

  // Initialize autocomplete when the component mounts
  useEffect(() => {
    // Don't initialize if Google Maps isn't loaded or if we already have an autocomplete instance
    if (!window.google || !window.google.maps || !window.google.maps.places || autocomplete) {
      return;
    }

    // Initialize autocomplete on the input element
    if (inputRef.current) {
      const newAutocomplete = new window.google.maps.places.Autocomplete(inputRef.current, {
        componentRestrictions: { country: 'us' }, // Optional: restrict to US addresses
        fields: ['formatted_address', 'geometry', 'name'],
        types: ['address'],
      });

      // Add listener for place selection
      newAutocomplete.addListener('place_changed', () => {
        const place = newAutocomplete.getPlace();
        if (place && place.formatted_address) {
          onChange(place.formatted_address);
        }
      });

      setAutocomplete(newAutocomplete);
    }
  }, [window.google, onChange]);

  // Prevent form submission on Enter key (let autocomplete handle it instead)
  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      e.preventDefault();
    }
  };

  return (
    <div>
      <label 
        htmlFor={id}
        style={{ display: 'block', marginBottom: '5px' }}
      >
        {label}
      </label>
      <input
        ref={inputRef}
        type='text'
        id={id}
        value={value}
        onChange={(e) => onChange(e.target.value)}
        onKeyDown={handleKeyDown}
        required={required}
        placeholder={placeholder}
        style={{ 
          width: '100%', 
          padding: '8px', 
          borderRadius: '4px', 
          border: '1px solid #ccc',
        }}
      />
    </div>
  );
};

export default AddressAutocomplete;




























