import React, { useState } from 'react';
import { GoogleMap, LoadScript, Marker, Circle } from '@react-google-maps/api';
import axios from 'axios';
import { AutoAccessorPropertyDeclaration } from 'typescript';

interface Suggestion {
    name: string;
    description: string;
    type: string;
}

interface MidpointResponse {
    midpoint: {
        lat: number;
        lng: number;
        address: string;
    };
    suggestions: Suggestion[];
}

// Map different suggestion types to colors for visual distinction
const typeColors: Record<string, string> = {
    restaurant: '#FF5733',
    cafe: '#C70039',
    park: '#44BD32',
    outdoor: '#44BD32',
    shopping: '#3498DB',
    museum: '#9B59B6',
    entertainment: '#F1C40F',
    sports: '#2E86C1',
    general: '#7F8C8D',
    cultural: '#8E44AD',
    landmark: '#D35400',
    education: '#16A085',
    default: '#4285F4'
};

const getTypeColor = (type: string): string => {
    return typeColors[type.toLowerCase()] || typeColors.default;
};

const MidpointFinder: React.FC = () => {
    const [address1, setAddress1] = useState('');
    const [address2, setAddress2] = useState('');
    const [radius, setRadius] = useState(5);
    const [midpoint, setMidpoint] = useState<{ lat: number; lng: number; address: string } | null>(null);
    const [suggestions, setSuggestions] = useState<Suggestion[]>([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState<string | null>(null);

    const mapContainerStyle = {
        width: '100%',
        height: '400px'
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setLoading(true);
        setError(null);

        try {
            console.log("Sending request:", { address1, address2, radius });

            // Use relative URL with proxy from package.json
            const res = await axios.post<MidpointResponse>('/api/midpoint/', {
                address1,
                address2,
                radius // Include radius in the request
            }, {
                headers: {
                    'Content-Type': 'application/json',
                }
            });

            console.log("API Response:", res.data);

            if (res.data && res.data.midpoint) {
                setMidpoint(res.data.midpoint);
                setSuggestions(res.data.suggestions || []);
            } else {
                throw new Error("Invalid response format");
            }
        } catch (error: any) {
            console.error('Error finding midpoint:', error);

            // Extract error message if available
            let errorMsg = 'Failed to find midpoint. Please try different addresses or try again later.';
            if (error.response && error.response.data && error.response.data.error) {
                errorMsg = error.response.data.error;
            }

            setError(errorMsg);
        } finally {
            setLoading(false);
        }
    };

    // Initialize Autocomplete when the component mounts
    React.useEffect(() => {
        if (window.google && window.google.maps) {
            initializeAutocomplete();
        }
    }, []);

    const initializeAutocomplete = () => {
        if (!window.google || !window.google.maps) return;

        const address1Input = document.getElementById('address1') as HTMLInputElement;
        const address2Input = document.getElementById('address2') as HTMLInputElement;

        if (address1Input && address2Input) {
            const autocomplete1 = new window.google.maps.places.Autocomplete(address1Input);
            const autocomplete2 = new window.google.maps.places.Autocomplete(address2Input);

            // Update state when an address is selected
            autocomplete1.addListener('place_changed', () => {
                const place = autocomplete1.getPlace();
                if (place.formatted_address) {
                    setAddress1(place.formatted_address);
                }
            });

            autocomplete2.addListener('place_changed', () => {
                const place = autocomplete2.getPlace();
                if (place.formatted_address) {
                    setAddress2(place.formatted_address);
                }
            });
        }
    };

    return (
        <div className='midpoint-finder' style={{ maxWidth: '800px', margin: '0 auto', padding: '20px' }}>
            <h1 style={{ textAlign: 'center' }}>Halfway</h1>
            <p style={{ textAlign: 'center' }}>Find the perfect place to meet your friends in the middle</p>

            <form onSubmit={handleSubmit} style={{ marginBottom: '20px' }}>
                <div className='form-group' style={{ marginBottom: '15px' }}>
                    <label htmlFor='address1' style={{ display: 'block', marginBottom: '5px' }}>Address 1:</label>
                    <input
                        type='text'
                        id='address1'
                        value={address1}
                        onChange={(e) => setAddress1(e.target.value)}
                        required
                        placeholder='Enter first address'
                        style={{ width: '100%', padding: '8px', borderRadius: '4px', border: '1px solid #ccc' }}
                    />
                </div>
                <div className='form-group' style={{ marginBottom: '15px' }}>
                    <label htmlFor='address2' style={{ display: 'block', marginBottom: '5px' }}>Address 2:</label>
                    <input
                        type='text'
                        id='address2'
                        value={address2}
                        onChange={(e) => setAddress2(e.target.value)}
                        required
                        placeholder='Enter second address'
                        style={{ width: '100%', padding: '8px', borderRadius: '4px', border: '1px solid #ccc' }}
                    />
                </div>
                <div className='form-group' style={{ marginBottom: '15px' }}>
                    <label htmlFor='radius' style={{ display: 'block', marginBottom: '5px' }}>
                        Search Radius: {radius} miles
                    </label>
                    <input
                        type='range'
                        id='radius'
                        min='1'
                        max='20'
                        value={radius}
                        onChange={(e) => setRadius(parseInt(e.target.value))}
                        style={{ width: '100%' }}
                    />
                    <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '12px' }}>
                        <span>1 mile</span>
                        <span>20 miles</span>
                    </div>
                </div>
                <button 
                    type='submit' 
                    disabled={loading}
                    style={{ 
                        width: '100%', 
                        padding: '10px', 
                        backgroundColor: '#4285F4', 
                        color: 'white', 
                        border: 'none', 
                        borderRadius: '4px',
                        cursor: loading ? 'not-allowed' : 'pointer'
                    }}
                >
                    {loading ? 'Finding Midpoint...' : 'Find Midpoint'}
                </button>
            </form>

            {error && (
                <div style={{ color: 'red', marginBottom: '20px', textAlign: 'center' }}>
                    {error}
                </div>
            )}

            {midpoint && (
                <div className='results'>
                    <h2 style={{ textAlign: 'center' }}>Meet in the Middle</h2>
                    <p className='midpoint-address' style={{ textAlign: 'center', marginBottom: '20px' }}>
                        <strong>Suggested meeting location: </strong>
                        {midpoint.address}
                    </p>

                    <div style={{ marginBottom: '30px' }}>
                        <LoadScript googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY || ''} libraries={['places']}>
                            <GoogleMap
                                mapContainerStyle={mapContainerStyle}
                                center={{ lat: midpoint.lat, lng: midpoint.lng }}
                                zoom={12}
                            >
                                <Marker 
                                    position={{ lat: midpoint.lat, lng: midpoint.lng }} 
                                    label={{
                                        text: 'M',
                                        color: 'white',
                                        fontWeight: 'bold'
                                    }}
                                />
                                {/* Add a circle to show the search radius */}
                                <Circle
                                    center={{ lat: midpoint.lat, lng: midpoint.lng }}
                                    radius={radius * 1609.34} // Convert miles to meters
                                    options={{
                                        fillColor: '#4285F4',
                                        fillOpacity: 0.1,
                                        strokeColor: '#4285F4',
                                        strokeOpacity: 0.8,
                                        strokeWeight: 2,
                                    }}
                                />
                            </GoogleMap>
                        </LoadScript>
                    </div>

                    <h3 style={{ textAlign: 'center' }}>
                        Things to do within {radius} miles
                    </h3>
                    {suggestions.length === 0 ? (
                        <p style={{ textAlign: 'center' }}>No suggestions available for this location.</p>
                    ) : (
                        <>
                            <div className='suggestions-filter' style={{ marginBottom: '20px', textAlign: 'center' }}>
                                <p style={{ fontSize: '14px', marginBottom: '10px' }}>
                                    Showing {suggestions.length} recommendations near {midpoint.address}
                                </p>
                            </div>
                            <div className='suggestions' style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '15px' }}>
                                {suggestions.map((suggestion, index) => (
                                    <div 
                                        key={index} 
                                        className='suggestion-card'
                                        style={{ 
                                            padding: '15px', 
                                            borderRadius: '8px',
                                            boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
                                            backgroundColor: '#f8f9fa',
                                            borderLeft: `4px solid ${getTypeColor(suggestion.type)}`
                                        }}
                                    >
                                        <h4 style={{ marginTop: 0, marginBottom: '8px' }}>{suggestion.name}</h4>
                                        <p style={{ fontSize: '14px', margin: '0 0 10px 0' }}>{suggestion.description}</p>
                                        <span 
                                            className='tag'
                                            style={{ 
                                                display: 'inline-block',
                                                padding: '4px 8px',
                                                backgroundColor: getTypeColor(suggestion.type),
                                                color: 'white',
                                                borderRadius: '4px',
                                                fontSize: '12px',
                                                textTransform: 'capitalize'
                                            }}
                                        >
                                            {suggestion.type}
                                        </span>
                                    </div>
                                ))}
                            </div>
                        </>
                    )}
                </div>
            )}
        </div>
    );
};

